-family: Consolas, &#39;Liberation Mono&#39;, Courier, monospace;font-size: 0.9em;break-inside: avoid;background-image: inherit;background-position: inherit;background-size: inherit;background-repeat: inherit;background-attachment: inherit;background-origin: inherit;background-clip: inherit;border-radius: 3px;padding: 8px 1em 6px;margin-bottom: 15px;margin-top: 15px;width: inherit;position: relative !important\">&nbsp;&lt;?php&nbsp;&nbsp;&nbsp;session_start();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//封装成类&nbsp;开启这些自动抓取文章&nbsp;&nbsp;&nbsp;#header(&quot;Refresh:30;http://news.zol.com.cn/more/2_1487.shtml:8080&quot;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;class&nbsp;SpiderTools{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*传入文章ID&nbsp;解析出文章标题*/&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;getBookNameById($aid){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//初始化curl&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$ch=&nbsp;curl_init();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//url&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$url=&#39;http://news.zol.com.cn/more/2_1487.shtml&#39;.$aid;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(is_numeric($aid)){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//正则表达式匹配&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$ru=&quot;/&lt;h1\\sclass=\\&quot;p-title\\&quot;&gt;\\s*&lt;a\\shref=\\&quot;\\/book\\/\\d+\\&quot;&gt;(.*)\\s*&lt;\\/a&gt;\\s*&lt;\\/h1&gt;/&quot;;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$ru=&quot;/&lt;title&gt;(.*)&lt;\\/title&gt;/&quot;;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//设置选项，包括URL&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_URL,&nbsp;$url);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($ch,&nbsp;CURLOPT_RETURNTRANSFE